import {useState,useEffect,useRef} from 'react';

function App(){
    const [content,setContent]=useState('')
    const [name,setName]=useState('')
    const [photo,setPhoto]=useState('')
    const inputRef=useRef()
    const fileRef=useRef()

    function getContent(){
        const value= inputRef.current.value

        fetch(`/home/${value}`).then((res)=>{
            return res.json()
        }).then((data)=>{
            console.log(data)
            setContent(data.data)
            setName(data.name)
            setPhoto(data.photo)
        })
    }

    function postContent(){
        const value= inputRef.current.value
        const file= fileRef.current.files[0]

        if(file!=null){
            const data = new FormData()
            data.append('file',file)
            data.append('name','test')
            data.append('filename',value)

            fetch(`/upload`,{
                method: 'POST',
                body: {"name":"raihan","age":21}
            }).then((response)=>{
                return response.json()
            }).then((data)=>{
                setPhoto(data.photo)
            })
        }
        else{
            setName("please Upload Photo")
        }

    }

    return(
        <div>
            <p>{content}</p>
            <input type='text' ref={inputRef}></input>
            <p>{name}</p>
            <img src={photo} alt="" />
            <input type='file' ref={fileRef}></input>
            <div>
                <button onClick={getContent}>get photo</button>
                <button onClick={postContent}>Upload Photo</button>
            </div>
        </div>
    )
}

export default App
