import React, { useState, useRef } from "react";

const AudioRecorder = () => {
  const [recording, setRecording] = useState(false);
  const [audioFiles, setAudioFiles] = useState([]);
  const [activeAudio, setActiveAudio] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioRefs = useRef([]);

  const toggleRecording = async () => {
    recording ? stopRecording() : await startRecording();
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      mediaRecorderRef.current.ondataavailable = (e) => audioChunksRef.current.push(e.data);
      mediaRecorderRef.current.onstop = saveRecording;
      mediaRecorderRef.current.start();
      setRecording(true);
    } catch (error) {
      console.error("Error accessing microphone:", error);
    }
  };

  const stopRecording = () => {
    mediaRecorderRef.current?.stop();
    setRecording(false);
  };

  const saveRecording = () => {
    const audioBlob = new Blob(audioChunksRef.current, { type: "audio/wav" });
    const file = new File([audioBlob], `recording_${Date.now()}.wav`, { type: "audio/wav" });
    const url = URL.createObjectURL(audioBlob);
    
    const data = new FormData();
    data.append("file", file);
    fetch("/uploadone", { method: "POST", body: data })
      .then((res) => res.json())
      .then((data) => console.log(data.msg))
      .catch((error) => console.error("Upload error:", error));
    
    setAudioFiles((prev) => [...prev, { url, file }]);
    audioChunksRef.current = [];
  };

  const playAudio = (index) => {
    if (activeAudio !== null && audioRefs.current[activeAudio]) {
      audioRefs.current[activeAudio].pause();
      audioRefs.current[activeAudio].currentTime = 0;
    }
    setActiveAudio(index);
    audioRefs.current[index].play();
  };

  return (
    <div>
      <h2>Audio Recorder</h2>
      <button onClick={toggleRecording}>{recording ? "Stop Recording" : "Start Recording"}</button>
      {audioFiles.length > 0 && (
        <div>
          <h3>Recordings</h3>
          {audioFiles.map(({ url, file }, index) => (
            <div key={index}>
              <p>{file.name}</p>
              <audio
                ref={(el) => (audioRefs.current[index] = el)}
                controls
                onPlay={() => playAudio(index)}
              >
                <source src={url} type="audio/wav" />
                Your browser does not support the audio element.
              </audio>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
