import {useState,useEffect,useRef} from 'react';
import AudioRecorder from './audiorecorder';

function App(){
    const [audio,setAudio]=useState('')
    const [output,setOutput]=useState('')
    const [language,setLanguage]=useState('')
    const [audioFile,setAudioFile]=useState(null)
    const [url,setUrl]=useState('')
    const [outputAudio,setOutputAudio]=useState(false)

    const inputRef=useRef()
    const fileRef=useRef()

    function test(){
        fetch('/members').then((res)=>{
            return res.json()
        }).then((data)=>{
            setOutput(data.data)
        })
    }

    function recieveAudio(data){
        setAudioFile(data.file)
        setUrl(data.url)
    }

    function sendMessage(){
        const data= new FormData()
        let isFile='n'
        let message=inputRef.current.value

        if(message!=''){
            data.append('type','text')
            data.append('text',message)
            data.append('language',language)
        }
        else{

        }
        const file= fileRef.current.files[0]
        if(file!=null){
            data.append('file',file)
            isFile='y'
        }
        data.append('isFile',isFile)

        fetch('/sendmessage',{
            method:'POST',
            body: data
        }).then((res)=>{
            return res.json()
        }).then((data)=>{
            setOutput(data.text)
            setAudio(data.audio)
        })
    }

    function sendMessageTest(){
        const data= new FormData()
        let isFile='n'
        let message=inputRef.current.value

        if(message!=''){
            data.append('type','text')
            data.append('text',message)
            data.append('language','ml-IN')
        }
        if(audioFile!=null){
            data.append('file',audioFile)
            data.append('type','audio')
            data.append('url',url)
            setAudioFile(null)
            setUrl('')
        }
        const file= fileRef.current.files[0]
        if(file!=null){
            data.append('file',file)
            isFile='y'
        }
        data.append('isFile',isFile)

        fetch('/fromuser',{
            method:'POST',
            body: data
        }).then((res)=>{
            res.blob().then((blob)=>{

            })
            res.json()
        }).then((data)=>{
            setOutput(data.data)
            setAudio(data.audio)
        })

        setOutputAudio(true)
    }

    return(
        <div>
            <input type='text' ref={inputRef}></input>
            <br/>
            <p>file</p>
            <input type='file' ref={fileRef}/>
            <br/>
            <button onClick={sendMessageTest}>send</button>
            <br/>
            <AudioRecorder sendToParent={recieveAudio}/>
            {url && (
              <div>
                <audio key={url} controls>
                  <source src={url} type="audio/wav" />
                  Your browser does not support the audio element.
                </audio>
              </div>
            )}
            {outputAudio && (
              <div>
                <audio controls>
                  <source src="/getfromfiles/output.wav" type="audio/wav" />
                  Your browser does not support the audio element.
                </audio>
              </div>
            )}
            <p>{audio}</p>
            <p>{output}</p>
        </div>
    )
}

export default App
